$(document).ready(function(){function copy(){$(".copy").click(function(){var text=$(this).siblings()[$(this).siblings().length-1].childNodes[0].innerText;const el=document.createElement('textarea');el.value=text;document.body.appendChild(el);el.select();document.execCommand('copy');document.body.removeChild(el);})
$(".just-copy").click(function(){var text=$(this).parent().siblings()[0].innerText;const el=document.createElement('textarea');el.value=text;document.body.appendChild(el);el.select();document.execCommand('copy');document.body.removeChild(el);})}
let code=document.querySelectorAll('pre'),copyIcon=document.createElement('span');copyIcon.innerHTML='<a class="just-copy" type="button" data-bs-toggle="tooltip" data-bs-placement="bottom" title="Copy to clipboard"><img src="/images/copy-icon.svg"/></a>';code.forEach((hl)=>{if(!hl.parentElement.classList.contains('code-snippet')&&!hl.parentElement.classList.contains('highlight')){hl.prepend(copyIcon.cloneNode([true]));}})
copy();});